import { LOG_OUT, LOG_IN} from '../Actions/index';

const INITIAL_STATE = {
  isAuth: false,
  name: null,
  id: null,
  moves: []
};

const Reducer = (state = INITIAL_STATE, { type, payload }) => {
  switch (type) {
    case LOG_IN:
      if (payload.login) {
        //Se almacena en el state las respuesta obtenida del server, en caso de afirmativa, el payload queda asi:
        // id: 1
        // login: true
        // username: "pedro"
        // token: "String"
        window.localStorage.setItem("token", payload.token);
        window.localStorage.setItem("isAuth", payload.login);
        window.localStorage.setItem("name", payload.name);
        window.localStorage.setItem("idUser", payload.id);
        return {
          ...state,
          isAuth: payload.login,
          name: payload.name,
          idUser: payload.idUser,
          moves: payload.moves,
          token: payload.token,
        };
      } else {
        const newState = {
          ...state,
          isAuth: false,
          message: payload.message,
        };
        return newState
      };
    case LOG_OUT:
      window.localStorage.removeItem("token");
      window.localStorage.removeItem("isAuth");
      window.localStorage.removeItem("name");
      window.localStorage.removeItem("idUser");
      return {
        ...state,
        isAuth: false,
        user: null,
        id: null,
        isAdmin: false,
        status: ""
      };
      case GET_MOVES:
          return {
              ...state,
              moves: payload.moves,
          }
    default:
      return state;
  };
};

export default Reducer;
