import styles from './Styles/form.module.css';

function Form({option}) {

    const title = option
    const submit = () => {
      e.preventDefault()
    }


    return (
        <div>
        <h3>{title}</h3>

        <form onSubmit={submit} className={styles.form}>

        <input name='concept' type="text" placeholder='Ingrese el concepto...' />
        <input name='mount' type="number" placeholder='Ingrese el monto...'/>
        <input name='date' type="date" defaultValue />
        <button type='submit'>Registrar movimiento</button>


        </form>
        </div>
    )
}

export default Form
