import React from 'react';
import styles from './Styles/home.module.css';
import { useState } from 'react';
import Form from './Form';



function Home() {
    const [state, setState] = useState({
        name: 'Santiago',
        moves: [],
    })

    const [entry, setEntry] = useState({
        text : true,
    })
    const [discharge, setDischarge] = useState({
        text : true,
    })
    const entryText = 'Quiero registrar un ingreso';
    const dischargeText = 'Quiero registrar un egreso';

    const changeformEntry = () => {
        if (entry) {
        setEntry(false)
        } else {
            setEntry(true)   
        }
    }

    const changeform = () => {
        if (entry) {
        setEntry(false)
        } else {
            setEntry(true)   
        }
    }



    return (
        <div className={styles.back}>
           <div className={styles.title}>
            Hola {state.name} !
           </div>
           <hr/>
           <div className={styles.moves}>
               <div className={styles.entry} onClick={changeformEntry}  >
               {entry ? <h3>{entryText} </h3> : <Form option='Ingresos'/>}
               </div>
               <div className={styles.discharge}>
                   {discharge ? <h3>{dischargeText} </h3> : <Form option='Egresos'/>}
                 </div>
           </div>
        </div>
    )
}

export default Home;
