import React from 'react';
import styles from './Styles/form.module.css';
import { useSelector , useDispatch} from 'react-redux';
import { useState } from 'react';
import { postMoves , getMoves} from '../Reducer/Actions';
import axios from 'axios';


function Form({option }) {
    const dispatch = useDispatch()
    const [state, setState] = useState({
      name: '',
      mount: 0,
      date:''
    })

  const postMoves = ({ name , mount , date  } , title , idUser) => {
      return function (dispatch) {  
        let type = title
        return axios.post('http://localhost:3001/users/moves',
        { name, mount, date , type , idUser})
        .then(r => {
            console.log(r);
            ;
        })
        .catch(error => console.log(error));
  }
}
    const idUser = useSelector(state => state.id)
    const title = option

    const handlesubmit = function () {
      postMoves(state,title,idUser)
    }

   const handleInputChange = function(e) {
    e.preventDefault();
    setState({
      ...state,
      [e.target.name]: e.target.value
    });
  }


    return (
        <div className={styles.container}>
        <h3 className={styles.title}>{title}</h3>
        <hr />
        <form onSubmit={handlesubmit} className={styles.form}>
        <input name='name' 
         value={state.name}
         onChange={(e) => handleInputChange(e)}
         className={styles.input}
         type="text"
         placeholder='Ingrese el concepto...' />
        <input name='mount'
         value={state.mount}
         onChange={(e) => handleInputChange(e)}
         className={styles.input}
         type="number" 
         placeholder='Ingrese el monto...'/>
        <input name='date'
         onChange={(e) => handleInputChange(e)}
         value={state.date}
         className={styles.input}
         type="date" 
          />

        <button name='submit'
        className={styles.button}
        type='submit'>Registrar movimiento</button>
        </form>
        </div>
    )
}

export default Form
